body
{
	font-size: 15px;
}

#footer
{
	display:none;
}

#header
{
	display: none;
}

#content
{
	padding: 10px;
}

#headerlabel
{
	font-size: 1.4em;
	background: #282828;
	padding: 10px;
	border-radius: 4px;

}

.category-button label
{
	display: none;
}

.category-button button
{
	transform: none;

	padding: 0px;
	color: #bbb;
	border-radius: 0px;
}


.category-button button:hover
{
	color: #eee;
}

.category-button button:active
{
	color: #fff;
	transform: scale(98%);

}

.category-button button:checked
{
	background: #999;
	color: #222;
}

.first-child button
{
	border-radius: 50% 0px 50% 0px;
}

.last-child button
{
	border-radius: 0% 50% 0% 50%;
}

.category-button button::before
{
	display:none;
}

.category-button button::after
{
	display:none;
}

.tag-button button
{
	width: auto;
	flex-grow: 0;
	padding: 0px 15px;
	border-radius: 50%;
	border: 1px solid #444;
	color: #888;
	margin: 3px;
	box-shadow: 0px 2px 3px rgba(0, 0, 0, 0.3);

}

.tag-button
{
	width: auto;
}

